/**
 * Given a string, determine if a permutation of the string could form a palindrome.

 For example,
 "code" -> False, "aab" -> True, "carerac" -> True.

 */

/**
 * Solution 1: 152ms;最简单的解法，使用一个visited list，判断如果不存在，则初始化为1，如果存在并为1，则--，如果存在并为0，则++；
 * 最终扫一遍visited list，如果总和>1，说明有多个单数，则无法构成回文。
 */
var canPermutePalindrome = function(s) {
    if(s.length == 0) return false;

    var list = new Array();
    for(var i = 0; i < s.length ; i++){
        if(typeof list[s[i]] == 'undefined') list[s[i]] = 1;
        else if(list[s[i]] == 1) list[s[i]]--;
        else if(list[s[i]] == 0) list[s[i]]++;
    }

    var res = 0;
    for(var x in list)
        if(list[x] == 1) res++

    return res <= 1;
};

var t1 = 'code';  //false
var t2 = 'aad';   //true
var t3 = 'carerac';  //true
var t4 = '';  //false
var t5 = "";

console.log(canPermutePalindrome(t1));
console.log(canPermutePalindrome(t2));
console.log(canPermutePalindrome(t3));
console.log(canPermutePalindrome(t4));
console.log(canPermutePalindrome(t5));